import { Component } from '@angular/core';
import { BookService } from '../../Services/BookService';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Author } from '../../Models/Author';
import { Book } from '../../Models/Book';
import { AuthorService } from '../../Services/AuthorService';

@Component({
  selector: 'app-book-create',
  templateUrl: './book-create.component.html',
  styleUrls: ['./book-create.component.css']
})
export class BookCreateComponent {
  authors!: Author[]
  book!: Book;
  isFormSubmit: boolean=false
  constructor(private bookService: BookService, private builder: FormBuilder,private authorService: AuthorService) { }
  myform: FormGroup = this.builder.group({
    id: [''],
    title: ['', Validators.required],
    description: ['', Validators.required],
    authorId: ['', Validators.required]
  })
  ngOnInit() {
    this.authorService.getAuthors().subscribe(t => {
      this.authors = t;
    });

  }
  onSubmit() {
    this.isFormSubmit = true;
    if (this.myform.get("authorId")?.value != "0") {
      this.book = { ...this.book, ...this.myform.value }
      console.log(this.book);
      this.bookService.createBook(this.book);
    }
  }
}
<div class="row">
  <div class="col-4"></div>
  <div class="col-4">
    <h2 class="text-center">Book</h2>
    <form (ngSubmit)="onSubmit()" [formGroup]="myform">
      <div>
        <label class="form-label w-100">
          Title:
          <div class="text-danger">Title is required!</div>
          <input class="form-control w-100" formControlName="title" required/>
        </label>
      </div>
      <div>
        <label class="form-label w-100">
          Author:
          <select class="form-control w-100" formControlName="authorId" >
            <option value="0">Choose author</option>
            <option *ngFor="let item of authors" [value]="item.id">{{item.name}}</option>
          </select>
        </label>
      </div>
      <div>
        <label class="form-label w-100">
          Description:
          <textarea class="form-control w-100" formControlName="description" required> </textarea>
        </label>
      </div>
      <div>
        <button type="submit" class="btn btn-success w-100">Create</button>
      </div>
    </form>
  </div>
  <div class="col-4"></div>
</div>
