import { Component } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Author } from '../../Models/Author';
import { AuthorService } from '../../Services/AuthorService';

@Component({
  selector: 'app-author-create',
  templateUrl: './create.component.html',
  styleUrls: ['./create.component.css']
})
export class CreateComponent {
  constructor(private formBuilder: FormBuilder,private authorService:AuthorService) { }
  authorForm!: FormGroup;
  author!: Author;
  ngOnInit() {
    this.author = new Author("", "", 0);
    this.authorForm = this.formBuilder.group({
      id: [''],
      name: ["", Validators.required],
      yearOfBirth: ['', Validators.required]
    });

  }
  onSubmit() {
    if (this.authorForm.valid) {
      this.author = { ...this.author, ...this.authorForm.value };
      console.log(this.author);
      this.authorService.createAuthor(this.author);
    }
    
  }
}

<div class="row">
  <div class="col-5"></div>
  <div class="col mt-5">
    <form [formGroup]="authorForm" (ngSubmit)="onSubmit()">
      <div>
        
        <label class="form-label">
          Name:
          <div class="text-danger" [hidden]="!isFormSubmitted || !authorForm.get('name')?.valid">
             Name is required!
          </div>
          <input class="form-control" id="name" formControlName="name" />
        </label>
      </div>
      <div>
        
        <label class="form-label">
          <div class="text-danger" *ngIf="authorForm.get('yearOfBirth')!.hasError('required')">
            Year of birth required!
          </div>
          Year of birth:
          <input class="form-control" id="yearOfBirth" formControlName="yearOfBirth" type="number" />
        </label>
      </div>
      <div>
        <button type="submit" class="btn btn-success">Create</button>
      </div>
    </form>

  </div>
  <div class="col-5"></div>
</div>
